let deferredPrompt;window.addEventListener("beforeinstallprompt",e=>{e.preventDefault(),deferredPrompt=e,document.querySelector(".add-app").classList.add("shown")}),window.addEventListener("load",()=>{const installButton=document.querySelector(".add-app button");installButton.addEventListener("click",()=>{installButton.parentElement.classList.remove("shown"),deferredPrompt.prompt(),deferredPrompt.userChoice.then(choiceResult=>{"accepted"===choiceResult.outcome?console.log("User accepted the A2HS prompt"):console.log("User dismissed the A2HS prompt"),deferredPrompt=null})}),window.addEventListener("appinstalled",()=>{console.log("App installed")})});